        "is_server": true if "server" in computer.operatingSystem|lower else false,
        "name": computer.computerName,
        "os_name": computer.operatingSystem,
        "last_contact": computer.lastProviderAgentEventDateUtc,
        "minutes_lastcontact": ((now('utc', '%Y-%m-%d %H:%M:%S')| load_datetime("%Y-%m-%d %H:%M:%S") - computer.lastProviderAgentEventDateUtc | load_datetime("%Y-%m-%dT%H:%M:%S.%fZ")).total_seconds()/60)|int
    }
    for computer in CTX.immy_computers
