{{
    {
        "id": computer.AgentId,
        "last_user": computer.LastLoggedInUser,
        "online": true if computer.Online != 0 else false,
        "is_server": true if "server" in computer.OperatingSystem | lower else false,
        "name": computer.ComputerName,
        "os_name": computer.OperatingSystem,
        "last_contact": computer.LastCheckInTime,
        "minutes_lastcontact": (
            (
                now('utc', '%Y-%m-%d %H:%M:%S') | load_datetime("%Y-%m-%d %H:%M:%S")
                -
                (
                    computer.LastCheckInTime | 
                    load_datetime(
                        "%Y-%m-%dT%H:%M:%S.%f" if "." in computer.LastCheckInTime else "%Y-%m-%dT%H:%M:%S"
                    )
                )
            ).total_seconds() / 60
        ) | int
    }
    for computer in CTX.kaseya_computers.Result
}}

{{
    {
        "id": computer.result.result.data.deviceId,
        "last_user": computer.result.result.data.lastLoggedInUser,
        "online":
        (
                (
                    (
                        now("utc", "%Y-%m-%d %H:%M:%S") | load_datetime("%Y-%m-%d %H:%M:%S")
                        - (
                            (
                                "1970-01-01T00:00:00.000Z"
                                if not computer.result.result.data.lastApplianceCheckinTime|d
                                else computer.result.result.data.lastApplianceCheckinTime
                            )
                            | load_datetime("%Y-%m-%dT%H:%M:%S.%fZ")
                        )
                    ).total_seconds() / 60
                ) | int
        ) < 10,
        "is_server": "server" in computer.result.result.data.supportedOsLabel | lower,
        "name": computer.result.result.data.longName,
        "os_name": computer.result.result.data.supportedOsLabel,
        "last_contact": (
            "1970-01-01T00:00:00.000Z"
            if computer.result.result.data.lastApplianceCheckinTime == ""
            else computer.result.result.data.lastApplianceCheckinTime
        ),
        "minutes_lastcontact": (
            (
                now("utc", "%Y-%m-%d %H:%M:%S") | load_datetime("%Y-%m-%d %H:%M:%S")
                - (
                    (
                        "1970-01-01T00:00:00.000Z"
                        if computer.result.result.data.lastApplianceCheckinTime == ""
                        else computer.result.result.data.lastApplianceCheckinTime
                    )
                    | load_datetime("%Y-%m-%dT%H:%M:%S.%fZ")
                )
            ).total_seconds() / 60
        ) | int
    }
    for computer in CTX.all_ncentral_wdetails
}}

