{%- set output = CTX.output|d({}) -%}
{%- do output.update(
    {
        "device_execution_results": CTX.device_results|d,
        "command_results": CTX.webhook_body|d
    }
) -%}
{{- output -}}

{%- do CTX.output.update({
    "device_results": CTX.device_results
    }) -%}
{{- CTX.output -}}

{%- set data_ns = namespace(log = [], status = 0) -%}
{%- for key, value in CTX()|items -%}
    {%- if key.startswith("log_") -%}
        {%- set entry = value %}
        {%- do entry.update({"task": key|replace("log_", "")}) -%}
        {%- do data_ns.log.append(entry) -%}
    {%- endif -%}
{%- endfor -%}

{%- macro status_code() -%}
    {%- set data_ns.status = [entry.status_code|d(0) for entry in data_ns.log]|sort(true)|first
    or 1000 -%}
    {%- if data_ns.status  > 1000 and data_ns.status  < 2000 -%}
        {%- set data_ns.status = 1001 -%}
    {%- elif data_ns.status > 2000 -%}
        {%- set data_ns.status = 2000 -%}
    {%- endif -%}
    {{- data_ns.status -}}
{%- endmacro -%}

{%- macro succeeded() -%}
    {{- data_ns.status < 2000  -}}
{%- endmacro -%}

{%- macro data() -%}
    {%- set data = {} -%}
    {%- for entry in data_ns.log -%}
        {%- if entry.data|d({}) or {} != {} -%}
            {%- do data.update(entry.data) -%}
        {%- endif -%}
    {%- endfor -%}
    {{- data or None -}}
{%- endmacro -%}

{%- macro warnings() -%}
{{-
    [
        entry for entry in data_ns.log
            if entry.status_code|d(0) > 1000 and entry.status_code|d(0) < 2000
    ]
-}}
{%- endmacro -%}

{%- macro errors() -%}
{{-
    [
        entry for entry in data_ns.log
            if entry.status_code|d(0) >= 2000
    ]
-}}
{%- endmacro -%}

{{-
    {
        "status_code": status_code(),
        "succeeded": succeeded(),
        "data": data(),
        "errors": errors(),
        "warnings": warnings(),
        "entries": data_ns.log
    }
-}}

